import React from 'react'
import { connect } from 'react-redux'
import { Card, Grid, Image } from 'semantic-ui-react'
import {selectMovie} from '../Redux/Actions/action';

const MovieCard = (props) => {

  const handleClick = () => {

  }
  const { movie } = props
      return(
            <Grid.Column>
              <Card.Group>
                <Card onClick={this.handleClick}>
                  <Image src={movie.preferredImage.uri}/>
                   <Card.Content header={movie.title} />
                   <Card.Content description={movie.shortDescription} />
               </Card>
              </Card.Group>
            </Grid.Column>

  )
}

const mapState = (state, ownProps) => {
  return {
    loadedMovies: state.loadedMovies,
    selected: state.selectedMovie.tmsId === ownProps.movie.tmsId
  }
};

const mapDispatch = dispatch => ({
  selectedMovie: (movie) => dispatch(selectMovie(movie))
})

export default connect(mapState, mapDispatch)(MovieCard);
