import React, { Component } from 'react'
import { connect } from 'react-redux';
import { zipMovies } from '../Redux/Actions/action';
import { withRouter } from 'react-router-dom'
import { Card, Form, Container, Header, Grid } from 'semantic-ui-react';
import '../App.css';

class Zips extends Component {
  state = {
    zip: "",
    radius: ""
  };

  handleChange = e => {
    console.log(e.target.value);
    this.setState({
      [e.target.name]: e.target.value
    })
  };

  handleSubmit = e => {
    e.preventDefault();
    let newZip = this.state.zip;
    this.props.zipMovies(newZip);
    this.props.history.push('/movies')
  };

  render(){
    let { zip } = this.state;
    return(
        <Grid >
          <Grid.Column className='form'>
              <Card fluid>
              <Header>Find Movies</Header>
              <Form onSubmit={this.handleSubmit}>
                  <Form.Input fluid type="text" placeholder="enter zip" name="zip" value={zip} onChange={this.handleChange}/>
                  <Form.Button content="submit"/>
              </Form>
              </Card>
          </Grid.Column>
        </Grid>
    )
  }
}

export default withRouter(connect(null, { zipMovies })(Zips))
