import React from 'react'
import { connect } from 'react-redux'
import { Card, Image } from 'semantic-ui-react'


const MovieCard = (props) => {

  const { movie } = props
      return(
      <Card>
        <Card.Content>
          <Card.Header>{movie.title}</Card.Header>
          <Card.Meta>
            <span className='srt-desc'>Short Description</span>
          </Card.Meta>
          <Card.Description>{movie.shortDescription}</Card.Description>
        </Card.Content>
      </Card>
  )
}

const mapState = (state) => {
  return {
    loadedMovies: state.loadedMovies
  }
};

export default connect(mapState)(MovieCard);
