const initialState = {
  user: {},
  nowPlaying: [],
  loadedMovies: [],
  selectedMovie: {}
};

const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'ADD_USER':{
      return {...state, user: action.payload}
    }
    case 'LOGOUT_USER':{
      state = undefined
    }
    default:
      return state
  }
};

export default userReducer
