import React, { Component } from 'react';
import {connect} from 'react-redux';
import { withRouter } from 'react-router-dom';
import { Header, Container } from 'semantic-ui-react';

class SelectedMovie extends Component {

  // componentDidMount() {
  //   if((this.props.selectedMovies) === undefined){
  //     this.props.history.push('/zips')
  //   }
  // }

  getMovieShowtimes = () => {

    let movieShowtimes = {};

    this.props.selectedMovie.showtimes.forEach(theatre => {
      if(movieShowtimes.hasOwnProperty([theatre.theatre.name])){
      movieShowtimes[theatre.theatre.name].push(theatre.dateTime)
      } else {
        movieShowtimes[theatre.theatre.name] = [theatre.dateTime]
      }
    });
    return movieShowtimes
  };

  render(){
    // console.log(this.getMovieShowtimes());
    return(
        <div>
          <Container>
            <Header size='huge'>{this.props.selectedMovie.title}</Header>
            <Header>Directed by:</Header>
              <h3>{this.props.selectedMovie.directors.map(director => <ul>{director}</ul>)}</h3>
            <Header>Staring:</Header>
              <h3> {this.props.selectedMovie.topCast.join(', ')}</h3>
            <Header>Synopsis:</Header>
              <h3>{this.props.selectedMovie.longDescription}</h3>
            <Header>Genre(s):</Header>
               <h4>{this.props.selectedMovie.genres.map(genre => <ul>{genre}</ul>)}</h4>
            {/*<p>this.props.selectedMovie.showtimes.map(showtime => director)</p>*/}
          </Container>
        </div>
    )
   }
};

const mapState = (state) => {
  return {
    selectedMovie: state.selectedMovie
  }
};

export default withRouter(connect(mapState)(SelectedMovie));
