export const addUser = (newUser) => ({type: 'ADD_USER', payload: newUser});

export const createUser = (newUser) => {
  return dispatch => {
    return fetch('http://localhost:3001/api/v1/users', {
      method: 'POST',
      headers: {
        'content-type': 'application/json',
        'accepts': 'application/json'
      },
      body: JSON.stringify({user: newUser})
    })
        .then(r => r.json())
        .then(newUser => {
          if(newUser.jwt){
          localStorage.setItem('token', newUser.jwt);
          dispatch(addUser(newUser.user))
          }
    }).catch(error => console.log(error))
  }
};

export const getUser =  (token) => {
  return dispatch => {
    return fetch('http://localhost:3001/api/v1/current_user', {
      method: 'GET',
      headers: {
        'content-type': 'application/json',
        'accepts': 'application/json',
        Authorization: `Bearer ${token}`
      }
    })
        .then(r => r.json())
        .then(currentUser => dispatch(addUser((currentUser))))
        .catch(console.error)
  }
};

export const login = (user) => {
  return dispatch => {
    return fetch('http://localhost:3001/api/v1/login', {
      method: 'POST',
      headers: {
        'content-type': 'application/json',
        'accepts': 'application/json'
      },
      body: JSON.stringify({user})
    })
        .then(r => r.json())
        .then(currentUser => {
          if(currentUser.jwt) {
            console.log('login current user :', currentUser);
            localStorage.setItem('token', currentUser.jwt);
            return dispatch(addUser(currentUser))
          }
    }).catch((console.error))
  }
};

export const getMovies = (moviesData) => ({type: 'GET_MOVIES', payload: moviesData});

export const zipMovies =  (zipData) => {
  return dispatch => {
    let currentDate = new Date().toISOString().slice(0,10);
    return fetch(`http://data.tmsapi.com/v1.1/movies/showings?startDate=${currentDate}&zip=${zipData}&radius=2&api_key=qp6xahv9m59kx2jambe8wvz7`, {
      method: 'GET',
      headers: {
        'content-type': 'application/json',
        'accepts': 'application/json'
      }
    })
    .then(r => r.json())
    .then(moviesData => dispatch(getMovies((moviesData))))
        .then(console.log)
    .catch(console.error)
  }
};

export const selectMovie = (movie) => ({type: 'SELECT_MOVIE', payload: movie});

export const getNowPlaying = () => {
  return dispatch => {
    return fetch('83a86e6cebd651357db4273964ce2199')
  }
}
