import React, { Component } from 'react';
import { connect } from 'react-redux';
import { zipMovies } from '../Redux/Actions/movieAction';
import { withRouter } from 'react-router-dom';
import { Form, Segment } from 'semantic-ui-react';
import '../App.css';

class Zips extends Component {
  state = {
    zip: "",
    radius: ""
  };

  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    })
  };

  handleSubmit = e => {
    e.preventDefault();
    let newZip = this.state.zip;
    let newRadius = this.state.radius;
    this.props.zipMovies(newZip, newRadius);
    this.props.history.push('/movies')
  };

  render(){
    let { zip, radius } = this.state;
    return(
        <Segment style={{top: '15vh'}} textAlign='center' id='form'>
              {/*<h1>Hi, {props.user.username}! Find Movies:</h1>*/}
                <Form onSubmit={this.handleSubmit} textAlign='center'>
                  <Form.Input style={{width: '500px'}} label="Enter Zip Code" fluid type="text" placeholder="enter zip code" name="zip" value={zip} onChange={this.handleChange}/>
                  <Form.Input style={{width: '500px'}} label="Enter Radius" fluid type="text" placeholder="enter radius" name="radius" value={radius} onChange={this.handleChange}/>
                  <Form.Button content="submit" primary/>
                </Form>
        </Segment>
    )
  }
}

const mapState = (state) => {
  return {
    user: state.userData.user
  }
};

export default withRouter(connect(mapState, { zipMovies })(Zips))
