import React, { Component } from 'react';
import { connect } from 'react-redux';
import { createUser } from '../Redux/Actions/action';
import { Card, Form, Grid, Header } from 'semantic-ui-react';
import '../App.css';

class SignUp extends Component {
  state = {
    username: "",
    password: ""
  };

  changeHandler = e => {
    this.setState({
      [e.target.name]: e.target.value
    })
  };

  submitHandler = e => {
    e.preventDefault();
    let newUser = this.state;
    this.props.createUser(newUser);
  };

  render() {
    let { username, password } = this.state;
    return (
        <div className='form'>
          <Grid textAlign='center' verticalAlign='middle'>
            <Grid.Column relaxed='very' stackable>
              <Card fluid>
                <Header>Sign Up</Header>
                <Form onSubmit={this.submitHandler}>
                  <Form.Input icon='user' iconPosition='left' type="text" placeholder="username" value={username} name="username" onChange={this.changeHandler}/>
                  <Form.Input icon='lock' iconPosition='left' type="password" placeholder="password" value={password} name="password" onChange={this.changeHandler}/>
                  <Form.Button content="submit"/>
                </Form>
              </Card>
            </Grid.Column>
          </Grid>
        </div>
    )
  }
}

// const mapState = (state) => {
//   console.log(state);
//   return {
//     user: state.user
//   }
// };

// const mapDispatch = (dispatch) => {
//   return {
//     createUser: (newUserObject) => dispatch({type: 'ADD_USER', payload: newUserObject})
//   }
// }

export default connect(null, {createUser})(SignUp)
