import React, { Component } from 'react';
import { Image, Divider, Container } from 'semantic-ui-react';
import logo from './logo.png';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom'
import Logout from './Logout';

class Header extends Component {

  render(){
    console.log('yo', this.props.user);
    return(
        <Container>
          {/*<Image src={logo} centered />*/}
          {/*<div className="logo"><b>N<span>ow</span>Pla<span>yi</span>ng</b></div>*/}
          <p><a href="https://en.wikipedia.org/wiki/Yellow">
            NowPlaying
          </a></p>
          { this.props.user ? <Logout floated='right'/> : null}
          <Divider/>
        </Container>
    )
  }
}

const mapState = (state) => {
  return{
    user: state.userData.user
  }
};

export default withRouter(connect(mapState)(Header))
