import React, { Component } from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom'
import MovieCard from '../Components/MovieCard'
import { Card } from 'semantic-ui-react';

class MoviesContainer extends Component {


  moviePosters = () => {
    let posterObj = {}
    this.props.nowPlaying.results.forEach(movie => {
      posterObj[movie.title] = `http://image.tmdb.org/t/p/w185/${movie.poster_path}`
    })
    return posterObj
  };

    render() {
      console.log((this.moviePosters()))
      let posters = this.moviePosters();
    let moviesArray = this.props.loadedMovies.map(movie => {
      return <MovieCard key={movie.tmsId} movie={movie} posters={posters[movie.title.replace("3D", "")]}/>;
    });
    return(
        <Card.Group itemsPerRow={3}>
            {moviesArray}
        </Card.Group>
    )
  }
}

const mapState = (state) => {
  console.log(state);
  return {
    loadedMovies: state.loadedMovies,
    nowPlaying: state.nowPlaying
  }
};

export default withRouter(connect(mapState)(MoviesContainer));
