const initialState = {
  user: {},
  loadedMovies: [],
  selectedMovie: {}
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case 'ADD_USER':{
      return {...state, user: action.payload}
    }
    case 'GET_MOVIES':{
      return {...state, loadedMovies: action.payload}
    }
    case 'SELECT_MOVIE':{
      return {...state, selectedMovie: action.payload}
    }
    default:
      return state
  }
};

export default reducer
