import React, { Component } from 'react'
import { connect } from 'react-redux';
import { createUser } from '../Redux/Actions/action'
import { Form } from 'semantic-ui-react'

class SignUp extends Component {
  state = {
    username: "",
    password: ""
  };

  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    })
  };

  handleSubmit = e => {
    e.preventDefault();
    let newUser = this.state;
    this.props.createUser(newUser);
    console.log(newUser)
  };

  render() {
    let { username, password } = this.state;
    return (
        <Form onSubmit={this.handleSubmit}>
          <Form.Group>
            <Form.Input type="text" placeholder='username' name='username' value={username} onChange={this.handleChange}/>
            <Form.Input type="password" placeholder='password' name='password' value={password} onChange={this.handleChange}/>
            <Form.Button content="submit"/>
          </Form.Group>
        </Form>
    )
  }
}

