import React, { Component } from 'react'
import { connect } from 'react-redux';
import { zipMovies } from '../Redux/Actions/action';
import { withRouter } from 'react-router-dom'
import { Card, Form, Grid, Label } from 'semantic-ui-react';
import '../App.css';

class Zips extends Component {
  state = {
    zip: "",
    radius: ""
  };

  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    })
  };

  handleSubmit = e => {
    e.preventDefault();
    let newZip = this.state.zip;
    let newRadius = this.state.radius;
    this.props.zipMovies(newZip, newRadius);
    this.props.history.push('/movies')
  };

  render(){
    let { zip, radius } = this.state;
    return(
        <div className='form'>
          <Grid textAlign='center' verticalAlign='middle'>
             <Grid.Column relaxed='very' stackable='true'>
              <Card fluid>
                <h1>Find Movies</h1>
                  <Form onSubmit={this.handleSubmit}>
                    <Form.Input label="Enter Zip Code" fluid type="text" placeholder="enter zip code" name="zip" value={zip} onChange={this.handleChange}/>
                    <Label basic color='red' pointing='left'>Please enter a value</Label>
                    <Form.Input label="Enter Radius" fluid type="text" placeholder="enter radius" name="radius" value={radius} onChange={this.handleChange}/>
                    <Label basic color='red' pointing='left'>Please enter a value</Label>
                    <Form.Button content="submit" primary/>
                  </Form>
              </Card>
            </Grid.Column>
          </Grid>
        </div>
    )
  }
}

export default withRouter(connect(null, { zipMovies })(Zips))
